mapper.insert:
insert into board 
(board_title, board_contents, board_write_date, board_fix_check, board_delete_check, emp_num, board_category_num, board_file_name, board_file_route)
value (#{board_title}, #{board_contents}, now(), #{board_fix_check}, #{board_delete_check}, #{emp_num}, #{board_category_num}, #{board_file_name}, #{board_file_route});

mapper.update:
update board 
set board_view_count = board_view_count + 1
where board_num=#{board_num};

mapper.insert:
	insert into board_comment
 (board_num, emp_num, comment_date, comment_contents)
 value (#{board_num},#{emp_num}, now(), #{comment_contents});

mapper.update:
update board_comment
set  comment_date = now(), comment_contents = #{comment_contents}
where comment_num =#{comment_num};
